"use client"

import { useState } from "react"
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet"
import "leaflet/dist/leaflet.css"
import { Icon } from "leaflet"

const icon = new Icon({
  iconUrl:
    "data:image/png;base64,",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41],
})

// Define the cities/towns around Lough Neagh
const locations = [
  { name: "Antrim", position: [54.7134, -6.2079], population: 23000, reports: 15 },
  { name: "Lurgan", position: [54.4634, -6.3335], population: 25000, reports: 22 },
  { name: "Cookstown", position: [54.6425, -6.745], population: 11000, reports: 8 },
  { name: "Magherafelt", position: [54.7553, -6.6075], population: 8800, reports: 12 },
  { name: "Portadown", position: [54.4195, -6.4538], population: 22000, reports: 18 },
  { name: "Toomebridge", position: [54.7508, -6.4605], population: 800, reports: 5 },
]

export default function LoughNeaghMap() {
  const [activeLocation, setActiveLocation] = useState<string | null>(null)

  return (
    <MapContainer
      center={[54.6, -6.4]}
      zoom={10}
      style={{ height: "600px", width: "100%" }}
      className="rounded-lg shadow-lg overflow-hidden"
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      {locations.map((location, index) => (
        <Marker
          key={location.name}
          position={location.position as [number, number]}
          icon={icon}
          eventHandlers={{
            click: () => setActiveLocation(location.name),
          }}
        >
          <Popup>
            <div className="text-center">
              <h2 className="text-lg font-semibold">{location.name}</h2>
              <p className="text-sm">Population: {location.population.toLocaleString()}</p>
              <p className="text-sm">Reports: {location.reports}</p>
            </div>
          </Popup>
          <div
            className={`absolute bg-white rounded-full w-8 h-8 flex items-center justify-center border-2 border-blue-500 text-blue-500 font-bold text-xs ${
              activeLocation === location.name ? "bg-blue-500 text-white" : ""
            }`}
            style={{ transform: "translate(-50%, -100%)" }}
          >
            {location.reports}
          </div>
        </Marker>
      ))}
    </MapContainer>
  )
}

